import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [cluster, setCluster] = useState('The Silent Doubter');
  const [result, setResult] = useState(null);
  const [aiResponse, setAiResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAnalyze = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        'https://heartbridge-api-backend.onrender.com/dynamic-recommendation',
        {
          text,
          cluster,
          quizAnswers: {}
        }
      );
      setResult(response.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ.');
    }
  };

  const askAI = async () => {
    try {
      const response = await axios.post(
        'https://heartbridge-api-backend.onrender.com/chat',
        { prompt: text }
      );
      setAiResponse(response.data.reply);
    } catch (err) {
      console.error(err);
      alert('ŸÅÿ¥ŸÑ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÄ Chat AI');
    }
  };

  return (
    <div style={{ padding: '40px', fontFamily: 'Arial' }}>
      <h2>ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿπŸÑÿßŸÇÿ© ‚Äì Heartbridge</h2>

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows={10}
        cols={70}
        placeholder="ÿßŸÉÿ™ÿ® ÿ≠Ÿàÿßÿ± ÿßŸÑÿπŸÑÿßŸÇÿ© ŸáŸÜÿß..."
        style={{ padding: '10px', fontSize: '16px' }}
      />

      <br />
      <button onClick={handleAnalyze} disabled={loading}>
        {loading ? 'Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ...' : 'ÿßÿ®ÿØÿ£ ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ'}
      </button>{' '}
      <button onClick={askAI}>ÿ™ÿ≠ÿØÿ´ ŸÖÿπ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä</button>

      {result && (
        <div style={{ marginTop: '30px' }}>
          <h3>üß† ÿßŸÑÿ™ŸàÿµŸäÿ©:</h3>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}

      {aiResponse && (
        <div style={{ marginTop: '30px', backgroundColor: '#f0f0f0', padding: '15px' }}>
          <h3>üí¨ ÿ±ÿØ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä:</h3>
          <p>{aiResponse}</p>
        </div>
      )}
    </div>
  );
}

export default App;
